from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase

# Set up all devices.
RainbowNinja = PrimeHub()
color_sensor = ColorSensor(Port.B)
color_sensor_2 = ColorSensor(Port.F)
motor = Motor(Port.C, Direction.COUNTERCLOCKWISE)
motor_2 = Motor(Port.D, Direction.CLOCKWISE)
NewRobot = DriveBase(motor, motor_2, 62.4, 79)
RightAttachment = Motor(Port.E, Direction.COUNTERCLOCKWISE)
LeftAttachment = Motor(Port.A, Direction.CLOCKWISE)


# The main program starts here.
NewRobot.use_gyro(True)
RainbowNinja.imu.reset_heading(0)
NewRobot.settings(straight_acceleration=350)
NewRobot.settings(straight_speed=150)
NewRobot.settings(turn_rate=90)
RightAttachment.run_angle(500, -95)
NewRobot.straight(50)
RightAttachment.run_angle(500, 95)
