from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.A, Direction.COUNTERCLOCKWISE)
motor_2 = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(motor, motor_2, 88, 144)


# The main program starts here.
drive_base.use_gyro(True)
prime_hub.imu.reset_heading(0)
drive_base.settings(straight_acceleration=350)
drive_base.settings(straight_speed=150)
drive_base.settings(turn_rate=90)
drive_base.straight(300)
drive_base.turn(-15)
drive_base.straight(450)
drive_base.turn(100)
