from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase

# Set up all devices.
DIRTblock = PrimeHub()
color_sensor = ColorSensor(Port.B)
color_sensor_2 = ColorSensor(Port.F)
motor_3 = Motor(Port.C, Direction.COUNTERCLOCKWISE)
motor_2_2 = Motor(Port.D, Direction.CLOCKWISE)
NewRobot = DriveBase(motor_3, motor_2_2, 62.4, 79)
RightAttachment = Motor(Port.A, Direction.COUNTERCLOCKWISE)
LeftAttachment = Motor(Port.E, Direction.CLOCKWISE)


# The main program starts here.
NewRobot.use_gyro(True)
DIRTblock.imu.reset_heading(0)
NewRobot.settings(straight_acceleration=350)
NewRobot.settings(straight_speed=250)
NewRobot.settings(turn_rate=90)
NewRobot.curve(1500, -5, Stop.NONE)
NewRobot.straight(200)
NewRobot.turn(20)
NewRobot.straight(100)
NewRobot.turn(-23)
NewRobot.straight(150)
NewRobot.turn(45)
NewRobot.straight(100)
NewRobot.turn(-115)
NewRobot.straight(900)
NewRobot.turn(-38)
NewRobot.straight(150)
NewRobot.turn(28)
NewRobot.straight(100)
NewRobot.turn(-33)
NewRobot.straight(100)
